	private LastFmRecommendedArtistsJsonConverter cut;

	@Before
	public void setUp()
	{
		cut = new LastFmRecommendedArtistsJsonConverter();
	}

	@Test
	public void testMultipleResults() throws ConverterException, JSONException
	{
		final JSONObject json = new JSONObject(MULTIPLE_RESULTS_JSON);
		final List<Artist> result = cut.convert(json);
		Assert.assertEquals(50, result.size());
		final Artist artist = result.get(0);
		Assert.assertEquals(EDaoType.LAST_FM_DIRECT, artist.getSource());
		Assert.assertEquals("Graham Coxon", artist.getName());
		Assert.assertNull(artist.getPopularity());
		Assert.assertEquals("http://www.last.fm/music/Graham+Coxon", artist.getLocation());
		Assert.assertEquals("fb56cc34-2ab1-4621-ac18-9bf2895e5f4f", artist.getId());
		Assert.assertNull(artist.getAlbumCount());
		Assert.assertEquals("http://userserve-ak.last.fm/serve/126/28997119.jpg", artist.getImageUri());
		Assert.assertEquals("fb56cc34-2ab1-4621-ac18-9bf2895e5f4f", artist.getAttribute(LastFmJsonConverter.LAST_FM_ARTIST_ID_ATTRIBUTE));
	}

